typedef struct _SECURITY_ATTRIBUTES {
    DWORD nLength;                // 结构体的size
    LPVOID lpSecurityDescriptor;  // 安全描述符指针 （ACES)
    BOOL bInheritHandle;        // 句柄对象是否继承
} SECURITY_ATTRIBUTES, *PSECURITY_ATTRIBUTES;

HANDLE CreateMutex(
