std::wstring ReadEnvironmentVariable(PCWSTR name) {
    DWORD count = ::GetEnvironmentVariable(name, nullptr, 0); // 先获取字符串的大小
    if (count > 0) {
        std::wstring value; // 申请一个buffer
        value.resize(count);
        ::GetEnvironmentVariable(name, const_cast(value.data()), count);
        // 获得value
        return value;
    }
    return L"";
}

