demo code: 写循环获得一个合适的buffer大小，
#include  
#include     
std::vector GetJobProcessList(HANDLE hJob) { 
  auto size = 256; 
  std::vector pids;  // vector
  while (true) { 
    auto buffer = std::make_unique(size); // buffer，使用make_unique申请的内存会自动释放。
    auto ok = ::QueryInformationJobObject(hJob, JobObjectBasicProcessIdList, buffer.get(), size, nullptr); 
       if (!ok && ::GetLastError() == ERROR_MORE_DATA) { // ERROR_MORE_DATA
     // buffer too small - resize and try again
     size *= 2;
     continue;
       } 

       if (!ok) break;

        auto info = reinterpret_cast(buffer.get());
      pids.reserve(info->NumberOfAssignedProcesses);  // 重新分配内存
    for (DWORD i = 0; i NumberOfAssignedProcesses; i++) 
      pids.push_back((DWORD)info->ProcessIdList[i]); 
    break;
    } 
  return pids;
}

